 sum += n - r; } Z ans = power(Z(2), sum); cout &lt;&lt; ans &lt;&lt; &quot;
&quot;;} L - Bit Sequence ç»Ÿè®¡ $x\in[0,L]$ ä½¿å¾—è¿ç»­ $m$ ä¸ªæ•° $x,x+1,\dots,x+m-1$ çš„ 1 çš„ä¸ªæ•°å¥‡å¶æ€§ï¼ˆå³ popcount çš„å¥‡å¶ï¼‰æ°å¥½ç­‰äºç»™å®šåºåˆ— $a_0\dots a_{m-1}$ $m \le 100, L \le 10^{18}, T \le 1000$ ç”±äº $m \le 100 \le 128$ï¼Œæšä¸¾ $x$ ä½ 7 ä½ï¼Œæšä¸¾è·Ÿåœ¨ä½ 7 ä½åé¢çš„ä¸€æ®µ 1 çš„å¥‡å¶æ€§ è¿™æ ·çš„è¯ï¼Œè®¨è®ºä¸‹æ˜¯å¦è¿›ä½ï¼Œå°±èƒ½çŸ¥é“ $f(x + i)$ çš„å¥‡å¶æ€§ æå‰æ•°ä½ dp ä¸€ä¸‹æ–¹æ¡ˆæ•°ï¼Œå³å¯ $O(128 \cdot 2 \cdot m)$ ç»Ÿè®¡ç­”æ¡ˆ æ—¶é—´å¤æ‚åº¦ $O(60 \cdot 2 \cdot 2 \cdot 2 + T \cdot 128 \cdot 2 \cdot m)$ 123456789101112131415161718192021222324252627282930313233343536373839404142434445464748495051525354int dp[61][2][2][2];void solve() { int m = read(), L = read(); vector&lt;int&gt; a(m); for (auto &amp;x : a) x = read(); memset(dp, 0, sizeof dp); dp[60][0][0][0] = 1; for (int i = 59; i &gt;= 7; i--) { int w = (L &gt;&gt; i &amp; 1); for (int o : {0, 1}) { for (int l : {0, 1}) { for (int x : {0, 1}) { if (!dp[i + 1][o][l][x]) continue; for (int y = 0; y &lt; (o | w) + 1; y++) { int no = o | (y &lt; w); int nl = y == 0 ? 0 : l ^ 1; int nx = x ^ y; dp[i][no][nl][nx] += dp[i + 1][o][l][x]; } } } } } int ans = 0; for (int i = 0; i &lt;= 127; i++) { // high xor low = a[0] // high = a[0] xor low int d = (__builtin_popcount(i) ^ a[0]) &amp; 1; for (int l : {0, 1}) { int ok = 1; for (int j = 1; j &lt; m and ok; j++) { int k = i + j; if (k &amp; 128) k = 128 * (l ^ 1) + (k &amp; 127); int nd = (__builtin_popcount(k) ^ a[j]) &amp; 1; ok &amp;= (nd == d); } if (ok) { ans += dp[7][1][l][d]; if (i &lt;= (L &amp; 127)) ans += dp[7][0][l][d]; } } } cout &lt;&lt; ans &lt;&lt; '
';} D - Fight against involution123456789101112131415161718192021222324252627282930void solve() { int n = read(); map&lt;int, int&gt; buk; vector&lt;int&gt; l(n), r(n); for (int i = 0; i &lt; n; i++) { l[i] = read(), r[i] = read(); } vector&lt;int&gt; p(n); ranges::iota(p, 0ll); ranges::sort(p, [&amp;](int i, int j) { if (r[i] != r[j]) return r[i] &lt; r[j]; return l[i] &gt; l[j]; // return r[i] &lt; r[j]; }); int t = 0, ans = 0; for (int i : p) { int L = l[i], R = r[i]; // cerr &lt;&lt; L &lt;&lt; ' ' &lt;&lt; R &lt;&lt; '
'; if (!buk.count(R)) { t = max(t, L); buk[R] = t; } ans += buk[R]; } cout &lt;&lt; ans &lt;&lt; '
';} C - Stone Game0 çš„ä¸ç”¨ç®¡ï¼Œ1-1 çš„ä»£ä»·æ˜¯ 1ï¼Œ2-2çš„ä»£ä»·æ˜¯ 4ï¼Œ1-2çš„ä»£ä»·æ˜¯ 2 å˜æˆ 0 é‚£ä¹ˆèƒ½ 1-2 ç»“åˆå°± 1-2 ç»“åˆå˜æˆ 0 ç„¶åä¸ç”¨ç®¡ å‰©ä¸‹çš„å°±æ˜¯åªæœ‰ 1 æˆ–è€…åªæœ‰ 2 çš„æƒ…å†µï¼Œæ‹¿ 2/3 äº’ç›¸è½¬æ¢ï¼Œæ¥å°½é‡è®© 1-2 æ“ä½œå˜å¤šå³å¯ 12345678910111213141516171819202122232425262728void solve() { int x = read(), y = read(), read(); int ans = 0; while (x &gt;= 3 or y &gt;= 3) { int t = min(x, y); ans += t * 2; x -= t, y -= t; // debug(x), debug(y); if (y) { int t = (y - y / 3) / 2; ans += t * 4; x += t, y -= t * 2; } else { int t = (x - x / 3) / 2; ans += t; y += t, x -= t * 2; } } if (x == 1 and y == 1) ans += 2; if (x == 2 and y == 1) ans += 2; if (x == 1 and y == 2) ans += 2; if (x == 2 and y == 2) ans += 4; cout &lt;&lt; ans &lt;&lt; '
