ot g_1 + (n-1)x \cdot g_2$ a_1 ä¹‹å’Œï¼š åœ¨ $a_n=x$ çš„å‰æä¸‹ï¼Œå‰©ä¸‹ $n-1$ ä¸ªå…ƒç´ å’Œä¸º $m-x$ã€‚ç”±å¯¹ç§°æ€§ï¼Œ$a_1$ çš„æ€»å’Œæ˜¯ $ rac{m-x}{n-1} \cdot g_1$ã€‚ æœ€å¤§å€¼ä¹‹åå…ƒç´ ä¹‹å’Œï¼š å¦‚æœ $a_{i-1}=x, i&lt;n$ï¼Œæˆ‘ä»¬éœ€è¦ $a_i$ çš„å’Œã€‚å‰©ä¸‹ $n-2$ ä¸ªå…ƒç´ å’Œä¸º $m-2x$ã€‚ç”±å¯¹ç§°æ€§ï¼Œ$a_i$ çš„å’Œæ˜¯ $ rac{m-2x}{n-2} \cdot g_2$ã€‚æœ‰ $n-2$ ä¸ªè¿™æ ·çš„ $i \in [2, n-1]$ã€‚æ€»å’Œæ˜¯ $(m-2x)g_2$ã€‚ å¦‚æœ $a_{n-1}=x$ï¼Œæˆ‘ä»¬éœ€è¦ $a_n$ çš„å’Œã€‚$a_n$ å›ºå®šä¸º $x$ã€‚æ•°ç»„æ•°é‡ä¸º $g_2$ã€‚æ€»å’Œæ˜¯ $x \cdot g_2$ã€‚ å°†è¿™äº›é¡¹é’ˆå¯¹æ¯ä¸ª $x$ ç´¯åŠ èµ·æ¥ï¼Œå°±å¾—åˆ°äº† $f(a)$ çš„æ€»å’Œã€‚ æ€»å¤æ‚åº¦ä¸º $\sum_{x=1}^m \mathcal{O}(m/x) = \mathcal{O}(m \log m)$ã€‚é¢„å¤„ç†é˜¶ä¹˜éœ€è¦ $\mathcal{O}(m+n)$ã€‚ 123456789101112131415161718192021222324252627282930313233343536373839404142434445464748495051525354555657585960616263646566676869707172737475767778798081828384858687888990919293949596979899100101102103104105106107108109110111112113114115116#include &lt;bits/stdc++.h&gt;using i64 = long long;using u64 = unsigned long long;using i128 = __int128;#define int i64#define pb push_back#define ep emplace#define eb emplace_backusing namespace std;int read(int x = 0, int f = 0, char ch = getchar()) { while (ch &lt; 48 or 57 &lt; ch) f = ch == 45, ch = getchar(); while (48 &lt;= ch and ch &lt;= 57) x = x * 10 + ch - 48, ch = getchar(); return f ? -x : x;}#define debug(x) cout &lt;&lt; #x &lt;&lt; &quot; = &quot; &lt;&lt; x &lt;&lt; &quot;
&quot;;#define vdebug(a) cout &lt;&lt; #a &lt;&lt; &quot; = &quot;; for (auto x : a) cout &lt;&lt; x &lt;&lt; &quot; &quot;; cout &lt;&lt; &quot;
&quot;;template &lt;class T1, class T2&gt; ostream &amp;operator&lt;&lt;(ostream &amp;os, const std::pair&lt;T1, T2&gt; &amp;a) { return os &lt;&lt; &quot;(&quot; &lt;&lt; a.first &lt;&lt; &quot;, &quot; &lt;&lt; a.second &lt;&lt; &quot;)&quot;; };template &lt;class T&gt; ostream &amp;operator&lt;&lt;(ostream &amp;os, const vector&lt;T&gt; &amp;as) { const int sz = as.size(); os &lt;&lt; &quot;[&quot;; for (int i = 0; i &lt; sz; i++) { if (i &gt;= 256) { os &lt;&lt; &quot;, ...&quot;; break; } if (i &gt; 0) { os &lt;&lt; &quot;, &quot;; } os &lt;&lt; as[i]; } return os &lt;&lt; &quot;]&quot;; }template &lt;class T&gt; void pv(T a, T b) { for (T i = a; i != b; i++) cerr &lt;&lt; *i &lt;&lt; &quot; &quot;; cerr &lt;&lt; '
'; }using pii = pair&lt;int, int&gt;;const int inf = 1e18;template &lt;class T&gt;constexpr T power(T a, i64 b) { T res {1}; for (; b; b /= 2, a *= a) if (b % 2) res *= a; return res; }constexpr i64 mul(i64 a, i64 b, i64 p) { i64 res = a * b - (i64)(1.L * a * b / p) * p; res %= p; if (res &lt; 0) res += p; return res; }template &lt;i64 P&gt;struct MInt { i64 x; constexpr MInt() : x {0} {} constexpr MInt(i64 x) : x {norm(x % getMod())} {} static i64 Mod; constexpr static i64 getMod() { return P &gt; 0 ? P : Mod; } constexpr static void setMod(i64 Mod_) { Mod = Mod_; } constexpr i64 norm(i64 x) const { if (x &lt; 0) x += getMod(); if (x &gt;= getMod()) x -= getMod(); return x; } constexpr i64 val() const { return x; } constexpr MInt operator-() const { MInt res; res.x = norm(getMod() - x); return res; } constexpr MInt inv() const { return power(*this, getMod() - 2); } constexpr MInt &amp;operator*=(MInt rhs) &amp; { if (getMod() &lt; (1ULL &lt;&lt; 31)) x = x * rhs.x % int(getMod()); else x = mul(x, rhs.x, getMod()); return *this; } constexpr MInt &amp;operator+=(MInt rhs) &amp; { x = norm(x + rhs.x); return *this; } constexpr MInt &amp;operator-=(MInt rhs) &amp; { x = norm(x - rhs.x); return *this; } constexpr MInt &amp;operator/=(MInt rhs) &amp; { return *this *= rhs.inv(); } friend constexpr MInt operator*(MInt lhs, MInt rhs) { MInt res = lhs; res *= rhs; return res; } friend constexpr MInt operator+(MInt lhs, MInt rhs) { MInt res = lhs; res += rhs; return res; } friend constexpr MInt operator-(MInt lhs, MInt rhs) { MInt res = lhs; res -= rhs; return res; } friend constexpr MInt operator/(MInt lhs, MInt rhs) { MInt res = lhs; res /= rhs; return res; } friend constexpr std::istream &amp;operator&gt;&gt;(std::istream &amp;is, MInt &amp;a) { i64 v; is &gt;&gt; v; a = MInt(v); return is; } friend constexpr std::ostream &amp;operator&lt;&lt;(std::ostream &amp;os, const MInt &amp;a) { return os &lt;&lt; a.val(); } friend constexpr bool operator==(MInt lhs, MInt rhs) { re